import { useState,useEffect } from "react";
import { useDispatch } from "react-redux";
import { addContact,getContact } from "../../actions/contactAction";
import shortid from "shortid";
import { useHistory } from "react-router-dom";
import { useParams } from "react-router-dom";

const EditContact = () => {
  const { id } = useParams();
  const history = useHistory();
  const dispatch = useDispatch();
  const [name, setName] = useState();
  const [phone, setPhone] = useState();
  const [email, setEmail] = useState();

  useEffect(() => {

    if(contact != null){
        setName(contact.name)
        setPhone(contact.phone)
        setEmail(contact.email)
    }
     dispatch(getContact(id));
  }, [contact]);


  return (
    <div className="card border-0 shadow w-50 m-auto create_contact">
      <div className="card-header font-bold bg-dark text-white text-center">
        Edit Contact
      </div>
      <div className="card-body">
        <form >
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              placeholder="Enter Your Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              placeholder="Enter Your Phone Number"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              placeholder="Enter Your Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-group text-center">
            <button
              className="btn font-bold bg-dark text-white mt-4"
              type="submit"
            >
              Edit Contact
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default EditContact;
