import { useState } from "react";
import { useDispatch } from "react-redux";
import { addContact } from "../../store";
import shortid from "shortid";

const CreateContact = () => {
  const dispatch = useDispatch();
  const [name, setName] = useState();
  const [phone, setPhone] = useState();
  const [email, setEmail] = useState();

  const CreateContact = (e) => {
    e.preventDefault();
    console.log(`my name is ${name} ${phone} ${email}`);
    const new_contact = {
      id: shortid.generate(),
      name: name,
      phone: phone,
      email: email,
    };
    dispatch(addContact(new_contact));
  };

  return (
    <div className="card border-0 shadow w-50 m-auto create_contact">
      <div className="card-header font-bold bg-dark text-white text-center">
        Add to Contact
      </div>
      <div className="card-body">
        <form onSubmit={(e) => CreateContact(e)}>
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              placeholder="Enter Your Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              placeholder="Enter Your Phone Number"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
            />
          </div>
          <div className="form-group">
            <input
              type="text"
              className="form-control"
              placeholder="Enter Your Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>
          <div className="form-group text-center">
            <button className="btn font-bold bg-dark text-white mt-4">
              Create Contact
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateContact;
